import Map from "./components/Map";
import Loader from './components/Loader'
import Header from './components/Header';
import { useState, useEffect } from "react";
import SelectionMenu from "./components/SelectionMenu";
import { MultiSelect } from "react-multi-select-component";
const options = [
  { label: 'WildFires ðŸ”¥', value: 'WildFires' },
  { label: 'Volcanoes ðŸŒ‹', value: 'Volcanoes' },
];

function App() {
  const [eventData, setEventData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState([]);
  const [Volcano, setVolcano] = useState(0);
  const [Wildfire, setWildfire] = useState(0);
  useEffect(() => {
    const fetchEvents = async () => {
      setLoading(true);
      const res = await fetch("https://eonet.gsfc.nasa.gov/api/v2.1/events");
      const { events } = await res.json();

      setEventData(events);
      setLoading(false);
    };

    fetchEvents();

    console.log(eventData)
  }, []);

  return (
    <div>
      <Header />
     {!loading ? <Map isVolcano={Volcano} isWildfire={Wildfire} eventData={eventData}/> : <Loader />}
     <div className="selection-menu">
        <h1>Select Disaster</h1>
        <pre>{JSON.stringify(selected)}</pre>
        <MultiSelect
          options={options}
          value={selected}
          onChange={setSelected}
          labelledBy="Select"
          shouldToggleOnHover={true}
        />
      </div>
    </div>
  );
}

export default App;
