import GoogleMapReact from "google-map-react";
import LocationMarker from "./LocationMarker";
import LocationInfoBox from "./LocationInfoBox";
import { useState } from "react";

const Map = ({ selected, eventData, center, zoom }) => {
  const [locationInfo, setLocationInfo] = useState(null);
  let Volcanoes = 0;
  let wildfires = 0;
  let Storms = 0;
  let Vol = 0;
  let Wild = 0;
  let Storm = 0;
  const checker = () => {
    selected.forEach((elem) => {
      if (elem.value === "V") {
        Vol = 1;
      } else if (elem.value === "W") {
        Wild = 1;
      } else if (elem.value === "S") {
        Storm = 1;
      }
    });
  };
  checker();
  console.log(`Vol is ${Vol}`);
  console.log(`Wild is ${Wild}`);
  const markers = eventData.map((ev) => {
    if (ev.categories[0].id === 8) {
        Wildfires += 1;
      return (
        <LocationMarker
          isWildfire={Wild}
          lat={ev.geometries[0].coordinates[1]}
          lng={ev.geometries[0].coordinates[0]}
          onClick={() => {
            setLocationInfo({ id: ev.categories[0].title, title: ev.title });
          }}
        />
      );
    } else if (ev.categories[0].id === 12) {
        Volcanoes += 1;
      return (
        <LocationMarker
          isVolcano={Vol}
          lat={ev.geometries[0].coordinates[1]}
          lng={ev.geometries[0].coordinates[0]}
          onClick={() => {
            setLocationInfo({ id: ev.categories[0].title, title: ev.title });
          }}
        />
      );
    } else if (ev.categories[0].id === 10) {
        return (
          <LocationMarker
            isStorm={Storm}
            lat={ev.geometries[0].coordinates[1]}
            lng={ev.geometries[0].coordinates[0]}
            onClick={() => {
              setLocationInfo({ id: ev.categories[0].title, title: ev.title });
            }}
          />
        );
      }

    return null;
  });

  return (
    <div>
      <div className="map">
        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyBdBvxU8-2HtDvYhPDUS2jyP5x9Ze2f5hw" }}
          defaultCenter={center}
          defaultZoom={zoom}
        >
          {markers}
        </GoogleMapReact>
      </div>
      {locationInfo && <LocationInfoBox info={locationInfo} />}
    </div>
  );
};

Map.defaultProps = {
  center: {
    lat: 42.3265,
    lng: -122.8756,
  },
  zoom: 2,
};

export default Map;
