import { Icon } from "@iconify/react";
import wildfire from "@iconify/icons-mdi/fire-alert";

const LocationMarker = ({ isVolcano = 0, isWildfire = 0, isStorm = 0, lat, lng, onClick }) => {
    let volmarker;
    let wildmarker;


  if (isVolcano) {
    volmarker = <Icon icon="emojione:volcano" className="location-icon" />;
  } 
  if (isWildfire) {
    wildmarker = <Icon icon={wildfire} className="location-icon" />;
  }
  if (isStorm) {
    wildmarker = <Icon icon="carbon:thunderstorm-severe" hFlip={true} />
  }

  return (
    <div className="location-marker" onClick={onClick}>
      {volmarker}
      {wildmarker}
    </div>
  );
};

export default LocationMarker;
